DELIMITER //
CREATE PROCEDURE InsertPoltronas(IN aeronaveId INT, IN numPoltronas INT)
BEGIN
    DECLARE i INT DEFAULT 1;
    DECLARE row_char CHAR(1);
    DECLARE seat_num INT;
    WHILE i <= numPoltronas DO
        SET row_char = CHAR(ASCII('A') + FLOOR((i - 1) / 6));
        SET seat_num = ((i - 1) MOD 6) + 1;
        SET i = i + 1;
    END WHILE;
END //
DELIMITER ;

CALL InsertPoltronas(1, 180);
